import torch.nn as nn


class MLP(nn.Module):
    def __init__(self, in_dim):
        super().__init__()

        self.mlp = nn.Sequential(
            nn.Linear(in_dim, in_dim // 2),
            nn.ReLU(),
            nn.Linear(in_dim // 2, 1),
            nn.Sigmoid(),
        )

    def forward(self, x):
        return self.mlp(x)
